#INSERT INTO `#__content` (`title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES ('Resources','resources','','<h3><a href=\"/resources\">Resources</a></h3>\r\n<ul>\r\n        <li class=\"animation\"><a href=\"/resources/animations/\">Animations</a></li>\r\n      <li class=\"course\"><a href=\"/resources/courses/\">Courses</a></li>\r\n       <li class=\"download\"><a href=\"/resources/downloads/\">Downloads</a></li>\r\n <li class=\"learningmodule\"><a href=\"/resources/learningmodules/\">Learning Modules</a></li>\r\n      <li class=\"note\"><a href=\"/resources/notes/\">Notes</a></li>\r\n     <li class=\"presentation\"><a href=\"/resources/onlinepresentations/\">Online Presentations</a></li>\r\n        <li class=\"publication\"><a href=\"/resources/publications/\">Publications</a></li>\r\n        <li class=\"series\"><a href=\"/resources/series/\">Series</a></li>\r\n <li class=\"teachingmaterial\"><a href=\"/resources/teachingmaterials/\">Teaching Materials</a></li>\r\n        <li class=\"workshop\"><a href=\"/resources/workshops/\">Workshops</a></li>\r\n</ul>','',1,0,0,0,NOW(),62,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00',NOW(),'0000-00-00 00:00:00','','','show_title=0\nlink_titles=\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',2,0,6,'','',0,0,'robots=\nauthor=');
#INSERT INTO `#__content` (`title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES ('Tools','tools','','<h3><a href=\"/resources/tools\">Tools</a></h3>\r\n<p class=\"tool\">We offer a wide variety of simulation tools available for use right in your browser! No installing or configurtion needed. <a href=\"/resources/tools\">More ...</a></p>\r\n<h3>Collaboration</h3>\r\n<ul>\r\n     <li class=\"group\"><a href=\"/groups\">User Groups</a><br />Share with your colleagues</li>\r\n        <li class=\"webmeetings\"><a href=\"/collaboration\">Collaboration</a><br />Other ways to collaborate</li>\r\n</ul>','',1,0,0,0,NOW(),62,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00',NOW(),'0000-00-00 00:00:00','','','show_title=0\nlink_titles=\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',1,0,1,'','',0,0,'robots=\nauthor=');
#INSERT INTO `#__content` (`title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`) VALUES ('Tags','tags','','<h3><a href=\"/tags\">Tags</a></h3>\r\n<p class=\"tag\">A tag is like a subject, keyword, or category. You can find content related to a particular subject by clicking a tag.</p>\r\n<ol class=\"tags\">\r\n      <li><a href=\"/tags/courselecture\">course lecture</a></li>\r\n <li><a href=\"/tags/cyberinfrastructure\">cyberinfrastructure</a></li>\r\n      <li><a href=\"/tags/experiments\">experiments</a></li>\r\n      <li><a href=\"/tags/researchseminar\">research seminar</a></li>\r\n</ol>','',1,0,0,0,NOW(),62,'','0000-00-00 00:00:00',1,0,'0000-00-00 00:00:00',NOW(),'0000-00-00 00:00:00','','','show_title=0\nlink_titles=\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',1,0,3,'','',0,0,'robots=\nauthor=');
