n	    );\r\n	  } else {  // displays image with links to Learn more & Get latest plug-in\r\n	    var alternateContent = \'\';\r\n	    document.write(alternateContent); \r\n          }\r\n	  // -->\r\n	</script>\r\n      </div>\r\n\r\n      <div class=\"instructions\">\r\n        <p>A download is a type of resource that users can download and use on their own computer.  It could be source code for a tool you developed, or even scripts for a book you have written.</p>\r\n        <h3>Related Resources &amp; Tips</h3>\r\n        <ul class=\"tips\">\r\n          <li><a href=\"/legal/licensing\">Intellectual Property Considerations</a></li>\r\n        </ul>\r\n        <h3>Examples</h3>\r\n        <p>Looking for an Example? Browse through the <a href=\"/resources/downloads/\">downloads</a> hosted on this site.</p>\r\n        <div id=\"middlespacer\"></div>\r\n      </div>\r\n      <div class=\"startcontribution\">\r\n        Ready? <a href=\"/index.php?option=com_contribute&step=1&type=9\">Start a Contribution Now!</a>\r\n      </div>\r\n    </div>\r\n</div>\r\n<div class=\"clear\"></div>','',1,0,NOW(),62,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00',NOW(),'0000-00-00 00:00:00','','','show_title=1\nlink_titles=\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',6,0,3,'','',0,'robots=\nauthor=');
INSERT INTO `#__content` (`title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`, `mask`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `metadata`) VALUES ('Contribute: Publications','publications','','{xhub:include type=\"stylesheet\" filename=\"howtocontribute.css\"}\r\n{xhub:include type=\"script\" filename=\"insertflash.js\"}\r\n\r\n<div class=\"breadcrumbs\">\r\n<a href=\"/contribute\" class=\"breadcrumbs\">Contribute</a> > <a href=\"/contribute/tools\" class=\"breadcrumbs\">Tools</a> | <a href=\"/contribute/presentations\" class=\"breadcrumbs\">Online Presentations</a> | <strong>Publications</strong> |  <a href=\"/contribute/downloads\" class=\"breadcrumbs\">Downloads</a> | <a href=\"/contribute/animations\" class=\"breadcrumbs\">Animations</a> | <a href=\"/contribute/teachingmaterials\" class=\"breadcrumbs\">Teaching Materials</a> \r\n</div>\r\n\r\n<div id=\"howtocontribute\">\r\n    <div class=\"howtopresentation\">\r\n      <script type=\"text/javascript\">\r\n        <!--\r\n\r\n	  var hasReqestedVersion = AC.DetectFlashVer(6, 6, 6);\r\n	\r\n	  if (hasReqestedVersion) {\r\n	    // embed the Flash Content SWF when all tests are passed\r\n	    AC.FL_RunContent(\r\n		\"src\", \"/site/demos/c_generic_fl\",\r\n		\"play\", \"true\",\r\n		\"width\", \"100%\",\r\n		\"height\", \"100%\",\r\n		\"align\", \"middle\",\r\n		\"wmode\", \"transparent\",\r\n		\"id\", \"flashrotation\",\r\n                \"scale\", \"noscale\",\r\n                \"bgcolor\", \"#990000\",\r\n		\"quality\", \"high\",\r\n		\"name\", \"flashrotation\",\r\n		\"allowScriptAccess\",\"sameDomain\",\r\n		\"type\", \"application/x-shockwave-flash\",\r\n		\'codebase\', \'https://fpdownload.macromedia.com/get/flashplayer/current/swflash.cab\',\r\n		\"pluginspage\", \"https://www.macromedia.com/go/getflashplayer\"\r\n	    );\r\n	  } else {  // displays image with links to Learn more & Get latest plug-in\r\n	    var alternateContent = \'\';\r\n	    document.write(alternateContent); \r\n          }\r\n	  // -->\r\n	</script>\r\n      </div>\r\n      <div class=\"instructions\">	\r\n        <p>A publication is a paper you have written, usually in PDF or DOC format.  The topic of the paper should be relevant to the content on this site, and may even include references to resources hosted here.</p>\r\n        <p><b>NOTE:</b> Please don\'t upload any publication if the copyright is owned by a publisher, without first obtaining explicit permission from the publisher.  If you have a preprint or an unpublished work, however, it can be uploaded here to help explain the details for a related tool, or as a reference for an online lecture.</p>\r\n	<h3>Related Resources &amp; Tips</h3>\r\n	<ul class=\"tips\">\r\n            <li><a href=\"/legal/licensing\">Intellectual Property Considerations</a></li>\r\n	</ul>\r\n	<h3>Examples</h3>\r\n	<p>Looking for an Example? Browse through the <a href=\"/resources/publications/\">publications</a> on this site.</p>\r\n        <div id=\"middlespacer\"></div>\r\n      </div>\r\n      <div class=\"startcontribution\">\r\n        Ready? <a href=\"/index.php?option=com_contribute&step=1&type=3\">Start a Contribution Now!</a>\r\n      </div>\r\n    </div>\r\n</div>\r\n\r\n<div class=\"clear\"></div>','',1,0,NOW(),62,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00',NOW(),'0000-00-00 00:00:00','','','show_title=1\nlink_titles=\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',5,0,2,'','',0,'robots=\nauthor=');
