layer\"\r\n	    );\r\n	  } else {  // displays image with links to Learn more & Get latest plug-in\r\n	    var alternateContent = \'\';\r\n	    document.write(alternateContent); \r\n          }\r\n	  // -->\r\n	</script>\r\n      </div>\r\n      <div class=\"instructions\">\r\n	<h3>Related Resources &amp; Tips</h3>\r\n	<ul class=\"tips\">\r\n	  <li><a href=\"http://www.nanohub.org/resources/240/\">Add Rappture to Your Software Development</a></li>\r\n          <li><a href=\"/search/?searchword=rappture\"><strong>All related tutorials</strong></a></li>\r\n	  <li><a href=\"/legal/licensing\">Intellectual Property Considerations</a></li>\r\n	</ul>\r\n	<h3>Examples</h3>\r\n	<p>Browse through the <a href=\"/resources/tools/\">simulation tools</a> hosted on this site.</p>\r\n	<p>Head over to our <a href=\"/tour/\">demo page</a> to see some demonstrations of simulation tools in action.</p>\r\n	<p>Visit our <a href=\"/projects\">project development area</a>. Each project has a source code control system for collaborative development, a bug tracking system, a wiki for project information, and more.</p>\r\n      </div>\r\n      <div class=\"startcontribution\">\r\n        Ready? <a href=\"/contribute/content/?task=start\">Start a Contribution Now!</a>\r\n      </div>\r\n    </div>\r\n</div>\r\n\r\n<div class=\"clear\"></div>','',1,0,NOW(),62,'','0000-00-00 00:00:00',0,0,'0000-00-00 00:00:00',NOW(),'0000-00-00 00:00:00','','','show_title=1\nlink_titles=\nshow_intro=1\nshow_section=0\nlink_section=0\nshow_category=0\nlink_category=0\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=0\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=\nkeyref=\nreadmore=',5,0,1,'','',0,'robots=\nauthor=');
